mapper.select:
SELECT coupon_id,coupon_name,coupon_money,fromtime,totime,use_scope,platform,
getstarttime,getendtime,integral_number,maxnumber,create_id,create_time,
update_id,update_time,state
FROM integral_coupon


        and coupon_name like "%"#{couponName}"%"


        and coupon_id=#{couponId}


        and coupon_money=#{couponMoney}


        and integral_number=#{integralNum}


        and fromtime>#{fromtime}


         and totime<#{totime} 


        and getstarttime>#{getstarttime}


         and getendtime<#{getendtime} 


ORDER BY update_time DESC

mapper.update:
UPDATE integral_coupon
integral_number=#{integralNum}, state=#{state}, update_id=#{updateId}, update_time=#{updatetime},
WHERE coupon_id=#{couponId}
